query:
[out:json][timeout:60];

/* 1) Residential and minor roads */
way["highway"~"^(residential|unclassified)$"](poly:"{poly_string}")->.set1;

/* 2) Footways and paths crossing the green space */
way["highway"~"^(footway|path)$"](poly:"{poly_string}")->.set2;

/* 3) Park / green recreational area */
way["leisure"="park"](poly:"{poly_string}")->.parks;

/* 4) Residential buildings (houses) */
way["building"~"^(house|residential)$"](poly:"{poly_string}")->.houses;

/* 5) Prominent individual trees */
node["natural"="tree"](poly:"{poly_string}")->.trees;

/* 6) Intersection: nodes common to roads and footways (≤10) */
node(w.set1)(w.set2)->.intersection;

/* Return only the key intersection nodes */
.intersection out body geom 10;
